mapper.insert:
/* kr.letech.study.cmmn.user.dao.UserDAO.insertUser */
INSERT INTO TB_USER
     (
       USER_ID
	 , USER_PW
	 , USER_NM
	 , RGST_ID
	 , RGST_DT
	 , UPDT_ID
	 , UPDT_DT
	 , DEL_YN
     )
       VALUES
     (
       #{userId}
	 , #{userPw}
	 , #{userNm}
	 , #{rgstId}
	 , NOW()
	 , #{updtId}
	 , NOW()
	 , 'N'
     )

		/* kr.letech.study.cmmn.user.dao.UserDAO.insertUser */

			SELECT IFNULL(MAX(FILE_NO) ,0)+1
    		  FROM TB_CMMN_FILE

		INSERT INTO TB_USER
		     (
		       FILE_GRP_ID
			 , FILE_NO
			 , ORGN_FILE_NM
			 , CONV_FILE_NM
			 , FILE_SIZE
			 , FILE_DIV
			 , RGST_ID
			 , RGST_DT
			 , UPDT_ID
			 , UPDT_DT
			 , DEL_YN
		     )
		       VALUES
		     (
		       #{fileGrpId}
			 , #{fileNo}
			 , #{orgnFileNm}
			 , #{convFileNm}
			 , #{fileSize}
			 , #{fileDiv}
			 , #{rgstId}
			 , NOW()
			 , #{updtId}
			 , NOW()
			 , 'N'
		     )

mapper.update:
/* kr.letech.study.cmmn.user.dao.UserDAO.updateUser */
UPDATE TB_USER
   SET USER_NM = #{userNm}
     , USER_PW = #{userPw}
     , UPDT_ID = #{updtId}
	 , UPDT_DT = NOW()
 WHERE USER_ID = #{userId}

/* kr.letech.study.cmmn.user.dao.UserDAO.deleteUser */
UPDATE TB_USER
   SET DEL_YN  = 'Y'
     , UPDT_ID = #{updtId}
	 , UPDT_DT = NOW()
 WHERE USER_ID = #{userId}
